use std::{
    collections::{BTreeMap, HashMap},
    fs,
    path::{Path, PathBuf},
};

use serde::{Deserialize, Serialize};
use thiserror::Error;

use crate::{
    asset_ident::AssetIdent,
    config::{Config, TargetType},
};

static MANIFEST_FILENAME: &str = "runway-manifest.toml";
static MANIFEST_FILENAME_LOCAL: &str = "runway-manifest.local.toml";
static HEADER_COMMENT: &str =
    "# This file was @generated by Runway. It is not intended for manual editing.\n";
static LOCAL_COMMENT: &str = "# This file contains local sync state and shouldn't be shared.\n";

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Manifest {
    pub runway_version: String,
    pub assets: BTreeMap<AssetIdent, AssetState>,
}
impl Default for Manifest {
    fn default() -> Self {
        Self {
            runway_version: env!("CARGO_PKG_VERSION").into(),
            assets: BTreeMap::new(),
        }
    }
}

impl Manifest {
    fn filter_scope(&self, config: &Config, local: bool) -> Self {
        let mut m = self.clone();
        for (_, asset) in m.assets.iter_mut() {
            for target in &config.targets {
                if (target.r#type == TargetType::Local) != local {
                    asset.targets.remove(&target.key);
                }
            }
        }
        m
    }

    fn read_from_file<P: AsRef<Path>>(
        folder_path: P,
        filename: &str,
    ) -> Result<Self, ManifestError> {
        let folder_path = folder_path.as_ref();
        let file_path = &folder_path.join(filename);

        let contents = fs::read(file_path)?;
        let config = toml::from_str(&String::from_utf8_lossy(&contents)).map_err(|s| {
            ManifestError::DeserializeToml {
                source: s,
                path: file_path.to_owned(),
            }
        })?;

        Ok(config)
    }

    // Merge manifest and local manifest
    pub fn read_from_folder<P: AsRef<Path>>(folder_path: P) -> Result<Self, ManifestError> {
        let mut main_manifest = match Manifest::read_from_file(&folder_path, MANIFEST_FILENAME) {
            Ok(m) => m,
            Err(e) => {
                if e.is_not_found() {
                    Manifest::default()
                } else {
                    return Err(e.into());
                }
            }
        };
        let local_manifest = match Manifest::read_from_file(&folder_path, MANIFEST_FILENAME) {
            Ok(m) => m,
            Err(e) => {
                if e.is_not_found() {
                    Manifest::default()
                } else {
                    return Err(e.into());
                }
            }
        };

        // Merge local into main
        for (ident, asset) in main_manifest.assets.iter_mut() {
            if let Some(local_asset) = local_manifest.assets.get(&ident) {
                asset.targets.extend(local_asset.targets.clone());
            }
        }

        // Add items only in local to main
        for (ident, asset) in local_manifest.assets {
            if !main_manifest.assets.contains_key(&ident) {
                main_manifest.assets.insert(ident, asset);
            }
        }

        Ok(main_manifest)
    }

    fn write_to_file<P: AsRef<Path>>(
        &self,
        folder_path: P,
        is_local: bool,
    ) -> Result<(), ManifestError> {
        match is_local {
            true => {
                let folder_path = folder_path.as_ref();
                let file_path = &folder_path.join(MANIFEST_FILENAME_LOCAL);

                let serialized =
                    HEADER_COMMENT.to_string() + LOCAL_COMMENT + &toml::to_string_pretty(self)?;
                fs::write(file_path, serialized)?;
            }
            false => {
                let folder_path = folder_path.as_ref();
                let file_path = &folder_path.join(MANIFEST_FILENAME);

                let serialized = HEADER_COMMENT.to_string() + &toml::to_string_pretty(self)?;
                fs::write(file_path, serialized)?;
            }
        }
        Ok(())
    }

    pub fn write(&self, config: &Config, folder_path: &Path) -> Result<(), ManifestError> {
        let main_manifest = self.filter_scope(&config, false);
        main_manifest.write_to_file(&folder_path, false)?;

        let local_manifest = self.filter_scope(&config, true);
        local_manifest.write_to_file(&folder_path, true)?;

        Ok(())
    }
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AssetState {
    pub targets: HashMap<String, TargetState>,
}

// For each asset and target pair, store the hash and ID of the last upload.
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct TargetState {
    pub hash: String,

    pub id: String,
}

#[derive(Debug, Error)]
pub enum ManifestError {
    #[error("Error deserializing TOML from path {}", .path.display())]
    DeserializeToml {
        path: PathBuf,
        source: toml::de::Error,
    },

    #[error(transparent)]
    SerializeToml {
        #[from]
        source: toml::ser::Error,
    },

    #[error(transparent)]
    Io {
        #[from]
        source: std::io::Error,
    },
}

impl ManifestError {
    pub fn is_not_found(&self) -> bool {
        match self {
            ManifestError::Io { source } => source.kind() == std::io::ErrorKind::NotFound,
            _ => false,
        }
    }
}
